import React from "react";
import './Login.css'
import { useNavigate } from 'react-router-dom';
import { useState, useRef } from 'react';


const Login = () => {
  const navigate = useNavigate();
  const [message] = useState('');
  const nameRef = useRef(undefined);
  const emailRef = useRef(undefined);
  const passwordRef = useRef(undefined);

  const handleSubmit = (event) => {
    event.preventDefault();

    const name = nameRef.current.value;
    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    localStorage.setItem("name", name)
    localStorage.setItem("email", email)
    localStorage.setItem("password", password)
    event.target.reset();
    navigate('/');
  };
  return (
    <>
      <div style={{ fontSize: "30px", textAlign: "center", margin: "1%" }}>Task 1 - Kishor Dabi</div>
      <div class="my-form bg-dark">
      <div style={{justifyItems: "center", padding: "20px", borderRadius: "20px", backgroundColor: "white"}}>  
        <h1>Login Form</h1>
        <form onSubmit={handleSubmit} style={{ borderRadius: "20px", backgroundColor: "#FFEBEE", padding: "20px" }}>

                <input
                  type="text"
                  id="email"
                  name="email"
                  class="email"
                  placeholder="Email"
                  ref={emailRef}
                />

                <input
                  type="text"
                  id="password"
                  name="password"
                  class="password"
                  placeholder="Password "
                  ref={passwordRef}
                />


                <br />
                <br />

                <button class="btn btn-primary" type="submit">Submit</button>

              </form>
        </div>
        <p class="mt-4">Not a member? <a href="/signup">Signup now</a></p>
      </div>
    </>
  )
};

export default Login;
