import React from "react";
import "./Home.css"
import { useEffect, useState } from "react"

const Home = () => {
  const [users, setUsers] = useState([])

  const fetchData = () => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setUsers(data)
      })
  }

  // console.log(users)

  useEffect(() => {
    fetchData()
  }, [])


  const sortOut = () => {
    users.sort((a, b) => {
      let fa = a.name.toLowerCase(),
        fb = b.name.toLowerCase();
      if (fa < fb) {
        return -1;
      }
      if (fa > fb) {
        return 1;
      }
      return 0;
    });

    localStorage.setItem("users", JSON.stringify(users))

    users.forEach((e) => {
      console.log(`${e.name}`);
    });
    let users1 = localStorage.getItem('users')
    console.log(users1);
    // setUsers(JSON.parse(users1))
  }


  return (
    <>
      <div style={{ fontSize: "3rem", textAlign: "center", margin: "1%" }}>Dashboard</div>
      <button onClick={(e) => sortOut()} >Sort</button>
      <table>
        <tr >
          <th >Sno</th>
          <th >name</th>
          <th>Email</th>
        </tr>
        {users.map((item, ind) => (
          <tr>
            <td>{item.id}</td>
            <td>{item.name}</td>
            <td>{item.email}</td>
          </tr>

        ))}
      </table>


    </>
  )

};

export default Home;
