import React from "react";
import './Login.css'
import { useNavigate } from 'react-router-dom';
import { useRef } from 'react';


const Login = () => {
  const navigate = useNavigate();

  const emailRef = useRef(undefined);
  const passwordRef = useRef(undefined);

  const handleSubmit = (event) => {
    event.preventDefault();


    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    let emailStored = localStorage.getItem("email")
    let passwordStored = localStorage.getItem("password")


    if (email === emailStored && password === passwordStored) {
      console.log("True Credentials")
      navigate('/home');
    } else {
      console.log(false)
    }

    event.target.reset();

  };
  return (
    <>
      <div style={{ fontSize: "30px", textAlign: "center", margin: "1%" }}>Task 1 - Kishor Dabi Sir</div>
      <div class="my-form bg-dark">
        <div style={{ justifyItems: "center", padding: "20px", borderRadius: "20px", backgroundColor: "white" }}>
          <h1>Login Form</h1>
          <form onSubmit={handleSubmit} style={{ borderRadius: "20px", backgroundColor: "#FFEBEE", padding: "20px" }}>

            <input
              type="text"
              id="email"
              name="email"
              class="email"
              placeholder="Email"
              ref={emailRef}
            />

            <input
              type="text"
              id="password"
              name="password"
              class="password"
              placeholder="Password "
              ref={passwordRef}
            />
            <br />
            <input type="checkbox" value="lsRememberMe" id="rememberMe" />
            <label for="rememberMe">Save password</label>
            <br />
            <br />
            <div id="captchaBackground">
              <span id="captcha">captcha text</span>
              <input id="textBox" type="text" name="text"/>
                <div id="buttons">
                  <input id="submit" type="submit"/>
                    <button id="refresh" type="submit">Refresh</button>
                </div>
                <span id="output"></span>
            </div>
            <button class="btn btn-primary" type="submit">Submit</button>

          </form>
        </div>
        <p class="mt-4">Not a member? <a href="/signup">Signup now</a></p>
      </div>
    </>
  )
};

export default Login;
